import { useContext } from "react";
import CommentsContext from "./CommentsContext";
import { Link } from "react-router-dom";
import DeleteComment from "./DeleteComment";
import { Image } from "react-bootstrap";

function DisplayComments() {
  let { commentsData } = useContext(CommentsContext);
  let { user } = useContext(CommentsContext);
  return commentsData.map((item, key) => (
    <div className="d-flex justify-content-between align-items-start py-1" key={key}>
      <div className="d-flex">
        <div>
          <Link
            to={
              item.commenter === user
                ? "/profile"
                : `/peopleProfile/${item.commenter}`
            }
            className="textLinkStyle"
          >
            <div className="d-flex">
              <Image
                src={
                  item.commenterPic
                    ? item.commenterPic
                    : "data:image/png;base64,"
                }
                alt=""
                width="30px"
                roundedCircle
                className="me-1"
              />
              <h5>{item.commenter}</h5>
            </div>
          </Link>
        </div>
        <div className="px-3">
          <h5>{item.comment}</h5>
        </div>
      </div>
      {item.commenter === user ? <DeleteComment index={key} /> : null}
    </div>
  ));
}

export default DisplayComments;
