import { useContext } from "react";
import FollowUnfollowButton from "./FollowUnfollowButton";
import ProfileContext from "./ProfileContext";
import { Link } from "react-router-dom";
import { ListGroup,Col,Row,Image } from "react-bootstrap";

function Followings() {
  let { followings } = useContext(ProfileContext);
  return (
    <Row>
      <Col xl={6} l={8} md={10}>
        <ListGroup className="w-100 rounded-top-0">
          <ListGroup.Item>
            <h5>{followings.length} followings</h5>
          </ListGroup.Item>
          {followings.map((item, key) => (
            <ListGroup.Item
              key={key}
              className="d-flex justify-content-between"
            >
              <Link
                to={`/peopleProfile/${item.email}`}
                className="textLinkStyle"
              >
                <div className="d-flex">
                  <Image
                    src={
                      item.profilePic
                        ? item.profilePic
                        : "data:image/png;base64,"
                    }
                    alt=""
                    width="30px"
                    width="30px"
                    roundedCircle
                  />
                  <h4 className="px-1">{item.email}</h4>
                </div>
              </Link>
              <FollowUnfollowButton name={item.email} purpose={"follow"} />
            </ListGroup.Item>
          ))}
        </ListGroup>
      </Col>
    </Row>
  );
}

export default Followings;
