import { useContext } from "react";
import FollowUnfollowButton from "./FollowUnfollowButton";
import ProfileContext from "./ProfileContext";
import { Link } from "react-router-dom";
import { Col, Row, ListGroup, Image } from "react-bootstrap";

function Followers() {
  let { followers } = useContext(ProfileContext);
  let { followings } = useContext(ProfileContext);

  return (
    <Row>
      <Col xl={6} l={8} md={10}>
        <ListGroup className="w-100 rounded-top-0">
          <ListGroup.Item>
            <h5>{followers.length} followers</h5>
          </ListGroup.Item>
          {followers.map((item, key) => (
            <ListGroup.Item
              key={key}
              className="d-flex justify-content-between"
            >
              <Link
                to={`/peopleProfile/${item.email}`}
                className="textLinkStyle"
              >
                <div className="d-flex align-items-start">
                  <Image
                    src={
                      item.profilePic
                        ? item.profilePic
                        : "data:image/png;base64,"
                    }
                    alt=""
                    width="30px"
                    he="30px"
                    roundedCircle
                  />
                  <h4 className="px-1 pt-1">{item.email}</h4>
                </div>
              </Link>
              <FollowUnfollowButton
                name={item.email}
                purpose={
                  followings.some(
                    (followingsItem) => followingsItem.email === item.email
                  )
                    ? "follow"
                    : "following"
                }
              />
            </ListGroup.Item>
          ))}
        </ListGroup>
      </Col>
    </Row>
  );
}

export default Followers;
