import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import dataGetterFunction from "./DateGetterFunction";
import Loading from "./Loading";
import BlueLoading from "./CommentsLoading";
import { Navigate } from "react-router-dom";
import { Form, Button, ListGroup, Image } from "react-bootstrap";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faSearch } from "@fortawesome/free-solid-svg-icons";
import "./App.css";
function Search() {
  let token = localStorage.getItem("token");
  let [data, setData] = useState([]);
  let [searchResult, setSearchResult] = useState([]);
  let [searchText, setSearchText] = useState("");
  let [searchClicker, setSearchClicker] = useState(0);
  let [loader, setLoader] = useState(true);
  let [searchLoader, setSearchLoader] = useState(false);
  let apiKey = process.env.REACT_APP_API_KEY;

  useEffect(() => {
    dataGetterFunction("search", setData, setLoader);
  }, []);

  useEffect(() => {
    async function search() {
      if (searchText.trim() === "") {
        return;
      }
      try {
        let searchResult = await axios.get(
          `0.1:500/searchResult/${searchText}`,
          {
            headers: {
              Authorization: token,
            },
          }
        );
        setSearchResult(searchResult.data.result);
        setSearchLoader(true);
      } catch (err) {
        console.log(err);
      } finally {
        setSearchLoader(false);
      }
    }
    search();
  }, [searchText, token, searchClicker, searchLoader]);

  return (
    <div style={{ minHeight: "90vh", maxHeight: "fit-content" }}>
      {data === "token error" ? (
        <Navigate to={"/"} />
      ) : loader ? (
        <Loading />
      ) : searchLoader ? (
        <BlueLoading />
      ) : (
        <div>
          <div className="d-flex m-3">
            <Form.Control
              type="text"
              onChange={(e) => {
                setSearchText(e.target.value);
              }}
              className="rounded-0"
            />
            <Button
              onClick={() => {
                setSearchClicker(searchClicker + 1);
              }}
              className="rounded-0"
            >
              <FontAwesomeIcon icon={faSearch} />
            </Button>
          </div>

          <ListGroup>
            {searchText.trim() === "" ? (
              <ListGroup.Item className="d-flex m-3 rounded-0">
                search someOne
              </ListGroup.Item>
            ) : (
              <ListGroup.Item className=" m-3 rounded-0">
                {!searchResult ? null : searchResult.length === 0 ? (
                  <h5>not found</h5>
                ) : (
                  searchResult.map((item, key) => (
                    <Link
                      to={`/peopleProfile/${item.email}`}
                      key={key}
                      className="textLinkStyle"
                    >
                      <div className="d-flex my-1">
                        <Image
                          src={
                            item.profilePic
                              ? item.profilePic
                              : "data:image/png;base64,"
                          }
                          alt=""
                          width={"60px"}
                          height={"60px"}
                          roundedCircle
                        />
                        <div className="px-3">
                          <h1>{item.email}</h1>
                          <p>{item.userName}</p>
                        </div>
                      </div>
                    </Link>
                  ))
                )}
              </ListGroup.Item>
            )}
          </ListGroup>
        </div>
      )}
    </div>
  );
}

export default Search;
